import React, { useState } from "react"
import AvatarPickerModal from "./AvatarPickerModal"
import { Image } from "antd"
import { Session } from '../../../models/auth/Session'
import { User } from "../../../models/auth/users/User"
import { UserType } from "../../../models/auth/user-types/UserType"
import { FieldType } from "../../../models/schema/FieldType"
import { ImageFieldInfo } from "../../../models/schema/custom-types/ImageFieldInfo"
import { PublishedStorageFile } from "../../../models/media/StorageFile"
import { useTranslations } from 'next-intl'

export const DefaultAvatarPath = "/assets/images/avatars/avatar1.png"
const AvatarFieldInfoName = "avatar"

type AvatarProps = {
	user: User
	userType: UserType
	session: Session
	onChange?(avatar: PublishedStorageFile): void
}

const Avatar = (props: AvatarProps) => {
	const [avatarPickerModalVisibility, setAvatarPickerModalVisibility] = useState<boolean>(false)

	const gloc = useTranslations()

	const onAvatarChangeButtonClick = () => {
		setAvatarPickerModalVisibility(true)
	}

	const handleUploadModalConfirm = (avatar: PublishedStorageFile) => {
		if (props.onChange) {
			props.onChange(avatar)
		}

		setAvatarPickerModalVisibility(false)
	};

	const handleUploadModalCancel = () => {
		setAvatarPickerModalVisibility(false)
	};

	const imageFieldInfo = props.userType.properties.find(x => x.name === AvatarFieldInfoName && x.type === FieldType.image) as ImageFieldInfo
	const avatar = (props.user as any).avatar

	if (imageFieldInfo && !imageFieldInfo.multiple) {
		return (
			<>
			<button type="button" onClick={(e) => { onAvatarChangeButtonClick() }} className="relative flex flex-col transition ease-in-out duration-100 active:scale-95 rounded-full">
				<span className="inline-block leading-[10px] overflow-hidden border-2 border-gray-100 dark:border-zinc-300 shadow-md dark:shadow-lg shadow-gray-500 dark:shadow-black rounded-full">
					<Image
						src={avatar ? avatar.url : DefaultAvatarPath}
						fallback="data:image/png;base64,"
						width={72}
						height={72}
						alt={props.user.username}
						preview={false}
					/>
				</span>

				<div className="absolute flex items-center justify-center bg-black bg-opacity-70 rounded-full left-0 top-0 right-0 bottom-0 opacity-0 hover:opacity-100">
					<span className="text-xs text-white font-semibold leading-tight">{gloc("Actions.Change")}</span>
				</div>
			</button>

			<AvatarPickerModal {...props} visibility={avatarPickerModalVisibility} onConfirm={(e) => {handleUploadModalConfirm(e)}} onCancel={handleUploadModalCancel} />
			</>
		)
	}
	else {
		return (
			<div className="relative flex rounded-full">
				<span className="inline-block leading-[10px] overflow-hidden border-2 border-gray-100 dark:border-zinc-300 shadow-md dark:shadow-lg shadow-gray-500 dark:shadow-black rounded-full">
					<Image
						src={avatar ? avatar.url : DefaultAvatarPath}
						width={72}
						height={72}
						alt={props.user.username}
						preview={false}
					/>
				</span>
			</div>
		)
	}
}

export default Avatar;