import React from "react"
import ReactPlayer from 'react-player'
import { Image } from 'antd'
import { FolderIcon, DocumentTextIcon, MusicNoteIcon } from "@heroicons/react/solid"
import { PublishedStorageFile } from "../../models/media/StorageFile"
import { FileHelper } from "../../helpers/FileHelper"

const iconClass = "w-full h-full max-w-[4.1rem] max-h-[4.1rem]"

export interface FilePreviewProps {
	file: PublishedStorageFile
	width?: number | string
	height?: number | string
	preview?: boolean
	className?: string
	onFileClick?(file: PublishedStorageFile): void
}

const FilePreview = (props: FilePreviewProps) => {
	const onFileClick = (file: PublishedStorageFile) => {
		if (props.onFileClick) {
			props.onFileClick(file)
		}
	}

	if (props.file.mimeType) {
		if (props.file.mimeType.startsWith("image/")) {
			return (
				<div className={props.className}>
					<div className="flex items-center justify-center rounded-sm overflow-hidden h-full">
						<Image
							width={props.width}
							height={props.height}
							src={props.file.url}
							alt={props.file.name}
							preview={props.preview}
							onClick={() => onFileClick(props.file)}
							fallback="data:image/png;base64,"
							placeholder={<div className="flex items-center justify-center w-full h-full"><svg xmlns="http://www.w3.org/2000/svg" className="animate-spin m-auto h-12 w-12" fill="none" viewBox="0 0 24 24"><circle className="stroke-gray-200 dark:stroke-zinc-700" cx="12" cy="12" r="10" strokeWidth="4"></circle><path className="fill-orange-500 dark:fill-orange-600" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path></svg></div>}
						/>
					</div>
				</div>
			)
		}
		else if (props.file.mimeType.startsWith("video/")) {
			return (
				<div className={props.className}>
					<div className="flex items-center justify-center rounded overflow-hidden w-full h-full">
						<ReactPlayer 
							url={props.file.url} 
							controls={true}
							width="100%"
							height="100%" />
					</div>
				</div>
			)
		}
		else if (props.file.mimeType.startsWith("audio/")) {
			return (
				<div className={props.className}>
					<div className="flex items-center justify-center rounded-sm overflow-hidden w-full h-full">
						<MusicNoteIcon className={`fill-blue-500 dark:fill-blue-600 ${iconClass}`} onClick={() => onFileClick(props.file)} />
					</div>
				</div>
			)
		}
		else if (FileHelper.isTextFile(props.file.mimeType)) {
			return (
				<div className={props.className}>
					<div className="flex items-center justify-center rounded-sm overflow-hidden w-full h-full">
						<DocumentTextIcon className={`fill-sky-500 dark:fill-sky-700 cursor-pointer ${iconClass}`} onClick={() => onFileClick(props.file)} />
					</div>
				</div>
			)
		}
		else {
			return (
				<div className={props.className}>
					<div className="flex items-center justify-center rounded-sm overflow-hidden w-full h-full">
						<DocumentTextIcon className={`fill-sky-500 dark:fill-sky-700 ${iconClass}`} onClick={() => onFileClick(props.file)} />
					</div>
				</div>
			)
		}
	}
	else if (props.file.kind === "Folder") {
		return (
			<div className={props.className}>
				<div className="flex items-center justify-center rounded-sm overflow-hidden w-full h-full">
					<FolderIcon className={`fill-amber-500 dark:fill-amber-600 ${iconClass} cursor-pointer`} onClick={() => onFileClick(props.file)} />
				</div>
			</div>
		)
	}
	else {
		return (
			<div className={props.className}>
				<div className="flex items-center justify-center rounded-sm overflow-hidden w-full h-full">
					<DocumentTextIcon className={`fill-sky-500 dark:fill-sky-700 ${iconClass}`} onClick={() => onFileClick(props.file)} />
				</div>
			</div>
		)
	}
}

export default FilePreview;