import React from "react"
import Head from "next/head"
import Link from "next/link"
import Dashboard from "../components/layouts/dashboard/Dashboard"
import { GetServerSideProps } from "next"
import { PageProps } from "../models/PageProps"
import { getValidatedServerSession } from "../models/auth/Session"
import { Image } from "antd"
import { useTranslations } from 'next-intl'

type HomeProps = {
	
};

export default function Home(props: HomeProps & PageProps) {
	const gloc = useTranslations()

	return (
		<>
			<Head>
				<title>{"ErtisAuth"}</title>
			</Head>

			<div className="flex flex-col overflow-hidden h-full">
				<div className="bg-neutral-50 dark:bg-zinc-900 border-b border-borderline dark:border-borderlinedark">
					<div className="flex items-center justify-between max-w-7xl 4xl:max-w-9xl mx-auto px-1 py-20">
						<div className="flex items-center gap-10">
							<span className="inline-block leading-[10px] overflow-hidden outline outline-2 outline-offset-2 outline-slate-400 dark:outline-zinc-700 shadow-lg shadow-gray-400 dark:shadow-black rounded-md">
								<Image
									src={props.session.user?.avatar || "/assets/images/avatars/avatar1.png"}
									fallback="data:image/png;base64,"
									width={80}
									height={80}
									alt={props.session.user.username}
									preview={false}
								/>
							</span>

							<h2 className="font-extrabold tracking-tight text-gray-900">
								<span className="block text-gray-700 dark:text-skin-white text-2xl">{gloc("Cms.Dashboard.WelcomeToErtisAuth")}</span>
								<span className="block text-skin-base text-lg leading-tight mt-0.5">{props.session.user.fullname}</span>
								<span className="text-sm text-gray-400 dark:text-zinc-400">{props.session.user.email_address}</span>
							</h2>
						</div>

						<div className="flex flex-col items-end mt-8 lg:mt-0 lg:flex-shrink-0">
							<Link href={`/auth/users/${props.session?.user._id}`} className="inline-flex items-center justify-center bg-white dark:bg-zinc-800 hover:bg-slate-50 dark:hover:bg-zinc-900 border border-gray-300 dark:border-zinc-400 text-sm font-medium text-skin-base dark:text-skin-white rounded px-8 py-2.5">
								{gloc("Cms.Dashboard.MyProfile")}
							</Link>
						</div>
					</div>
				</div>

				<div className="h-full">
					<div className="flex flex-col max-w-7xl 4xl:max-w-9xl mx-auto gap-12">
						<Dashboard session={props.session} />
					</div>
				</div>
			</div>
		</>
	)
}

export const getServerSideProps: GetServerSideProps<HomeProps & PageProps> = async (context) => {
	const session = getValidatedServerSession(context.req, context.res)
	return {
		props: {
			session
		}
	};
};